// src/pages/HomePage.js
import React, { useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
import Sidebar from '../components/Sidebar.js';
import '../CalendarStyles.css';

// Moment configuration to start from Monday -> Idea: Make this changable in settings
moment.updateLocale("en", {
  week: {
    dow: 1,
  },
});

const localizer = momentLocalizer(moment);

const events = [];

const HomePage = () => {
    const [isSidebarMinimized, setIsSidebarMinimized] = useState(false);
    const [currentDate, setCurrentDate] = useState(new Date());
  
    const toggleSidebar = () => {
      setIsSidebarMinimized((prevState) => !prevState);
    };
  
    const handlePreviousMonth = () => {
      setCurrentDate((prevDate) => moment(prevDate).subtract(1, "month").toDate());
    };
  
    const handleNextMonth = () => {
      setCurrentDate((prevDate) => moment(prevDate).add(1, "month").toDate());
    };
  
    const handleToday = () => {
      setCurrentDate(new Date());
    };
  
    return (
        <div className="flex h-screen">
          {/* Sidebar */}
          <Sidebar
            isMinimized={isSidebarMinimized}
            onToggleSidebar={toggleSidebar}
          />
    
          {/* Main Content */}
          <div className="flex-1 flex flex-col">
            {/* Header */}
            <div className="p-4 flex items-center justify-between bg-gray-100 border-b">
              <h1 className="text-xl font-bold">Calendar</h1>
              <div className="flex items-center gap-4">
                <button
                  className="px-4 py-2 bg-customPurple text-white rounded hover:bg-[#8060DB]"
                  onClick={handleToday}
                >
                  Today
                </button>
                <button
                  className="px-4 py-2 bg-gray-300 rounded hover:bg-gray-400"
                  onClick={handlePreviousMonth}
                >
                  ←
                </button>
                <span className="text-lg">
                  {moment(currentDate).format("MMMM YYYY")}
                </span>
                <button
                  className="px-4 py-2 bg-gray-300 rounded hover:bg-gray-400"
                  onClick={handleNextMonth}
                >
                  →
                </button>
                <button
                  className="px-4 py-2 bg-customPurple text-white rounded hover:bg-[#8060DB]"
                >
                  Add New Event
                </button>
              </div>
            </div>
    
            {/* Calendar */}
            <div className="flex-1">
              <Calendar
                localizer={localizer}
                events={events}
                startAccessor="start"
                endAccessor="end"
                date={currentDate}
                onNavigate={(date) => setCurrentDate(date)}
                style={{ height: "calc(100vh - 80px)" }}
              />
            </div>
          </div>
        </div>
      );
    };
    
    export default HomePage;
