import React, { useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
import Sidebar from "../components/Sidebar.js";
import "../CalendarStyles.css";

moment.updateLocale("en", {
  week: {
    dow: 1,
  },
});

const localizer = momentLocalizer(moment);

const HomePage = () => {
  const [isSidebarMinimized, setIsSidebarMinimized] = useState(false);
  const [currentDate, setCurrentDate] = useState(new Date());

  const toggleSidebar = () => {
    setIsSidebarMinimized((prevState) => !prevState);
  };

  const handlePreviousMonth = () => {
    setCurrentDate((prevDate) => moment(prevDate).subtract(1, "month").toDate());
  };

  const handleNextMonth = () => {
    setCurrentDate((prevDate) => moment(prevDate).add(1, "month").toDate());
  };

  const handleToday = () => {
    setCurrentDate(new Date());
  };

  return (
    <div className="flex">
      {/* Sidebar */}
      <Sidebar isMinimized={isSidebarMinimized} toggleSidebar={toggleSidebar} />

      {/* Main Content */}
      <div className="flex-1 p-4">
        <div className="flex items-center justify-between mb-4">
          {/* Left-aligned group */}
          <div className="flex items-center space-x-8">
            <h1
              className="text-[20px] font-semibold"
              style={{ fontFamily: "'Inter', sans-serif" }}
            >
              Calendar
            </h1>
            <button
              onClick={handleToday}
              className="text-customPurple hover:underline"
            >
              Today
            </button>
            <button onClick={handlePreviousMonth} className="px-2">
              ←
            </button>
            <button onClick={handleNextMonth} className="px-2">
              →
            </button>
            <span className="text-gray-600">
              {moment(currentDate).format("MMMM YYYY")}
            </span>
          </div>

          {/* Right-aligned button */}
          <button
            className="bg-customPurple text-white px-4 py-2 rounded-lg hover:bg-[#8060DB]"
            onClick={() => console.log("Add New Event clicked")}
          >
            Add New Event
          </button>
        </div>

        <Calendar
          localizer={localizer}
          events={[]}
          startAccessor="start"
          endAccessor="end"
          date={currentDate}
          onNavigate={(date) => setCurrentDate(moment(date).toDate())}
          style={{ height: "calc(100vh - 100px)" }}
        />
      </div>
    </div>
  );
};

export default HomePage;
