import React, { useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
import Sidebar from '../components/Sidebar.js';
import '../CalendarStyles.css';

// Moment configuration
moment.updateLocale("en", {
  week: {
    dow: 1,
  },
});

const localizer = momentLocalizer(moment);

const HomePage = () => {
  const [isSidebarMinimized, setIsSidebarMinimized] = useState(false);
  const [currentDate, setCurrentDate] = useState(new Date());

  const toggleSidebar = () => {
    setIsSidebarMinimized((prev) => !prev);
  };

  const handlePreviousMonth = () => {
    setCurrentDate((prev) => moment(prev).subtract(1, "month").toDate());
  };

  const handleNextMonth = () => {
    setCurrentDate((prev) => moment(prev).add(1, "month").toDate());
  };

  const handleToday = () => {
    setCurrentDate(new Date());
  };

  return (
    <div className="flex">
      {/* Sidebar */}
      <Sidebar isSidebarMinimized={isSidebarMinimized} toggleSidebar={toggleSidebar} />

      {/* Main Content */}
      <div className={`main-content ${isSidebarMinimized ? "minimized" : ""}`}>
        <div className="flex items-center justify-between mb-4"
        style={{ height: "69px" }}
        >
          <div className="flex items-center space-x-8">
            <h1 className="text-[20px] font-semibold" style={{ fontFamily: "'Inter', sans-serif" }}>
              Calendar
            </h1>
            <button
  onClick={handleToday}
  className="text-[14px] font-normal px-10 py-2 rounded-[4px]"
  style={{
    fontFamily: "'Inter', sans-serif",
    border: "1px solid #E9EBEF",
  }}
>
  Today
</button>
            <button onClick={handlePreviousMonth} className="px-2">←</button>
            <button onClick={handleNextMonth} className="px-2">→</button>
            <span className="text-gray-600">{moment(currentDate).format("MMMM YYYY")}</span>
          </div>
          <button className="bg-customPurple text-white px-4 py-2 rounded-lg hover:bg-[#8060DB]">
            Add New Event
          </button>
        </div>
        <Calendar
          localizer={localizer}
          events={[]}
          startAccessor="start"
          endAccessor="end"
          date={currentDate}
          onNavigate={(date) => setCurrentDate(moment(date).toDate())}
          style={{ height: "calc(100vh - 100px)" }}
          components={{
            toolbar: () => null, // This removes the toolbar
          }}
        />
      </div>
    </div>
  );
};

export default HomePage;
