// src/pages/HomePage.js
import React, { useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
import Sidebar from '../components/Sidebar.js'; // Adjust the path as needed
import '../CalendarStyles.css'; // Ensure you have this file for custom styles

// Configure moment to start the week on Monday
moment.updateLocale("en", {
  week: {
    dow: 1, // Monday is the first day of the week
  },
});

const localizer = momentLocalizer(moment);

const events = [];

const HomePage = () => {
    const [isSidebarMinimized, setIsSidebarMinimized] = useState(false);
  
    return (
      <div className="flex h-screen">
        {/* Sidebar */}
        <div
          className={`transition-all duration-300 ${
            isSidebarMinimized ? "w-16" : "w-64"
          }`}
        >
          <Sidebar
            isMinimized={isSidebarMinimized}
            toggleSidebar={() => setIsSidebarMinimized(!isSidebarMinimized)}
          />
        </div>
  
        {/* Main Content */}
        <div className="flex-1 p-4">
          <h1 className="text-xl font-bold mb-4">Home Page</h1>
          <div className="h-full">
            <Calendar
              localizer={localizer}
              events={events}
              startAccessor="start"
              endAccessor="end"
              className="h-full"
            />
          </div>
        </div>
      </div>
    );
  };

export default HomePage;
