// src/pages/HomePage.js
import React, { useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
import Sidebar from '../components/Sidebar.js';
import '../CalendarStyles.css';

// Moment configuration to start from Monday -> Idea: Make this changable in settings
moment.updateLocale("en", {
  week: {
    dow: 1,
  },
});

const localizer = momentLocalizer(moment);

const events = [];

const HomePage = () => {
    const [isSidebarMinimized, setIsSidebarMinimized] = useState(false);
    const [currentDate, setCurrentDate] = useState(moment());
  
    const handleSidebarToggle = () => {
      setIsSidebarMinimized(!isSidebarMinimized);
    };
  
    const handleTodayClick = () => {
      setCurrentDate(moment());
    };
  
    const handlePrevMonth = () => {
      setCurrentDate((prevDate) => prevDate.clone().subtract(1, "month"));
    };
  
    const handleNextMonth = () => {
      setCurrentDate((prevDate) => prevDate.clone().add(1, "month"));
    };
  
    return (
        <div className="flex h-screen">
        {/* Sidebar */}
        <Sidebar isMinimized={isSidebarMinimized} onToggle={handleSidebarToggle} />
  
        {/* Main Content */}
        <div
          className="flex-1 flex flex-col"
          style={{ marginLeft: isSidebarMinimized ? "64px" : "256px" }}
        >
          {/* Header Area */}
          <div className="p-4 border-b flex items-center justify-between bg-white">
            <div className="flex items-center gap-4">
              <h1 className="text-2xl font-bold">Calendar</h1>
  
              <button
                onClick={handleTodayClick}
                className="px-4 py-2 bg-customPurple text-white rounded hover:bg-[#8060DB]"
              >
                Today
              </button>
  
              <button
                onClick={handlePrevMonth}
                className="px-4 py-2 bg-gray-300 rounded hover:bg-gray-400"
              >
                &larr;
              </button>
  
              <button
                onClick={handleNextMonth}
                className="px-4 py-2 bg-gray-300 rounded hover:bg-gray-400"
              >
                &rarr;
              </button>
  
              <span className="text-lg font-medium">
                {currentDate.format("MMMM YYYY")}
              </span>
            </div>
  
            <button
              className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
              onClick={() => console.log("Add new event clicked")}
            >
              Add New Event
            </button>
          </div>
  
          {/* Calendar Area */}
          <div className="flex-1 p-4">
            <Calendar
              localizer={localizer}
              events={events}
              startAccessor="start"
              endAccessor="end"
              style={{ height: "calc(100vh - 72px)" }}
              date={currentDate.toDate()}
              onNavigate={(date) => setCurrentDate(moment(date))}
            />
          </div>
        </div>
      </div>
    );
  };

  export default HomePage;
