// src/pages/HomePage.js
import React from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
import Sidebar from '../components/Sidebar.js'; // Adjust the path as needed
import '../CalendarStyles.css'; // Ensure you have this file for custom styles

// Configure moment to start the week on Monday
moment.updateLocale("en", {
  week: {
    dow: 1, // Monday is the first day of the week
  },
});

const localizer = momentLocalizer(moment);

const events = [];

const HomePage = () => {
    const [isSidebarMinimized, setIsSidebarMinimized] = useState(false);
  
    return (
      <div className="full-screen-container">
        {/* Sidebar */}
        <div className="sidebar-container">
          <Sidebar
            isMinimized={isSidebarMinimized}
            toggleSidebar={() => setIsSidebarMinimized(!isSidebarMinimized)}
          />
        </div>
  
        {/* Main Content */}
        <div
          className={`main-content-container ${
            isSidebarMinimized ? "ml-16" : "ml-64"
          }`}
        >
          <h1 className="text-xl font-bold mb-4">Home Page</h1>
          <Calendar
            localizer={localizer}
            events={events}
            startAccessor="start"
            endAccessor="end"
            className="full-screen-calendar"
          />
        </div>
      </div>
    );
  };
  
  export default HomePage;
