// src/pages/HomePage.js
import React, { useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
import Sidebar from '../components/Sidebar.js'; // Adjust the path as needed
import '../CalendarStyles.css'; // Ensure you have this file for custom styles

// Configure moment to start the week on Monday
moment.updateLocale("en", {
  week: {
    dow: 1, // Monday is the first day of the week
  },
});

const localizer = momentLocalizer(moment);

const events = [];

const HomePage = () => {
    const [isSidebarMinimized, setIsSidebarMinimized] = useState(false);
  
    return (
      <div className="flex h-screen">
        {/* Sidebar */}
        <Sidebar
          isMinimized={isSidebarMinimized}
          toggleSidebar={() => setIsSidebarMinimized(!isSidebarMinimized)}
        />
  
        {/* Main Content */}
        <div
          className="flex-1 h-full"
          style={{
            marginLeft: isSidebarMinimized ? "64px" : "256px",
            transition: "margin-left 0.3s ease",
          }}
        >
          <Calendar
            localizer={localizer}
            events={events}
            startAccessor="start"
            endAccessor="end"
            className="h-full w-full"
          />
        </div>
      </div>
    );
  };
  
  export default HomePage;
