// src/components/Sidebar.js
import React, { useState } from "react";
import { Calendar, Settings, Home } from "react-feather"; // Example icons
import { Link } from "react-router-dom";

const Sidebar = () => {
  const [isMinimized, setIsMinimized] = useState(false);

  return (
    <div
      className={`h-screen bg-customPurple text-white ${
        isMinimized ? "w-16" : "w-64"
      } transition-all duration-300`}
    >
      <button
        onClick={() => setIsMinimized(!isMinimized)}
        className="p-4 focus:outline-none"
      >
        {isMinimized ? "▶️" : "◀️"}
      </button>

      <nav className="mt-4 space-y-4">
        <Link
          to="/"
          className="flex items-center p-4 hover:bg-[#8060DB] rounded-md"
        >
          <Home size={20} className="mr-4" />
          {!isMinimized && <span>Home</span>}
        </Link>
        <Link
          to="/calendar"
          className="flex items-center p-4 hover:bg-[#8060DB] rounded-md"
        >
          <Calendar size={20} className="mr-4" />
          {!isMinimized && <span>Calendar</span>}
        </Link>
        <Link
          to="/settings"
          className="flex items-center p-4 hover:bg-[#8060DB] rounded-md"
        >
          <Settings size={20} className="mr-4" />
          {!isMinimized && <span>Settings</span>}
        </Link>
      </nav>
    </div>
  );
};

export default Sidebar;
